// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.17;

// Uncomment this line to use console.log
// import "hardhat/console.sol";
import "./interfaces/IErc721.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155URIStorage.sol";
import "./interfaces/IErc1155.sol";

contract Robot {
    string private __version;
    string private __salt;
    event Buy(
        address indexed contractAddress,
        uint256 indexed cType,
        uint256 indexed tokenId,
        address from,
        address to,
        uint256 amount,
        uint256 price
    );

    // 1155

    event Mint1155(
        uint256 indexed tokenId,
        uint256 indexed amount,
        address indexed owner
    );

    // 721

    mapping(uint256 => uint256) sell721List;

    event Mint721(
        address indexed c,
        uint256 indexed tokenId,
        uint256 indexed collectionId,
        address createBy,
        address to
    );

    // event Creator(address indexed account, uint8 indexed rate);

    event Sell721(
        address indexed c,
        uint256 indexed tokenId,
        uint256 indexed price
    );

    event CancelSell721(address indexed c, uint256 indexed tokenId);

    constructor(string memory _versoion, string memory _salt) {
        __version = _versoion;
        __salt = _salt;
    }

    function version() public view returns (string memory) {
        return __version;
    }

    function bytesToHex(bytes memory buffer)
        public
        pure
        returns (string memory)
    {
        // Fixed buffer size for hexadecimal convertion
        bytes memory converted = new bytes(buffer.length * 2);

        bytes memory _base = "0123456789abcdef";

        for (uint256 i = 0; i < buffer.length; i++) {
            converted[i * 2] = _base[uint8(buffer[i]) / _base.length];
            converted[i * 2 + 1] = _base[uint8(buffer[i]) % _base.length];
        }

        return string(abi.encodePacked("0x", converted));
    }

    function uint2str(uint256 _i)
        internal
        pure
        returns (string memory _uintAsString)
    {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k - 1;
            uint8 temp = (48 + uint8(_i - (_i / 10) * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    // 1155
    function batchLock1155(
        address erc1155,
        address[] memory tos,
        uint256[] memory tokenIds,
        string[] memory names,
        string[] memory tokenURIs,
        uint256[] memory amounts,
        uint256[] memory supplies,
        string[] memory suppliesHash
    ) external payable {
        require(
            (tos.length == tokenIds.length) &&
                (tokenIds.length == amounts.length) &&
                (amounts.length == tokenURIs.length) &&
                (tokenURIs.length == names.length),
            "length not same"
        );
        for (uint256 index = 0; index < tos.length; index++) {
            require(
                IErc1155(erc1155).balanceOf(tos[index], tokenIds[index]) +
                    amounts[index] <=
                    supplies[index]
            );
            string memory s = bytesToHex(
                bytes.concat(
                    keccak256(
                        abi.encodePacked(
                            __salt,
                            uint2str(tokenIds[index]),
                            uint2str(supplies[index])
                        )
                    )
                )
            );
            require(
                keccak256(abi.encodePacked(s)) ==
                    keccak256(abi.encodePacked(suppliesHash[index])),
                "supply hash is error"
            );
            emit Mint1155(tokenIds[index], amounts[index], tos[index]);
            IErc1155(erc1155).mint(
                tos[index],
                tokenIds[index],
                names[index],
                tokenURIs[index],
                amounts[index]
            );
        }
    }

    function batchMint721(
        address erc721,
        address[] memory accounts,
        string[] memory tokenURIs,
        uint256[] memory collectionIds
    ) public {
        require(
            (accounts.length == tokenURIs.length) &&
                (tokenURIs.length == collectionIds.length),
            "length not same"
        );
        for (uint256 index = 0; index < accounts.length; index++) {
            uint256 nextId = IErc721(erc721).currentId() + 1;
            emit Mint721(
                erc721,
                nextId,
                collectionIds[index],
                msg.sender,
                accounts[index]
            );
            IErc721(erc721).mint(accounts[index], tokenURIs[index]);
        }
    }

    // address[][] memory creatorAddresses,
    // uint8[][] memory creatorRates
    // 混合
    function batchAll(
        address[] memory addresses,
        uint256[] memory types,
        string[] memory orderIds,
        string[] memory ordersHash,
        address[] memory fromAddresses,
        address[] memory tos,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        string[] memory tokenURIs,
        string[] memory names,
        uint256[] memory values,
        uint256[] memory supplies,
        string[] memory suppliesHash
    ) public payable {
        require(
            (addresses.length == types.length) &&
                (types.length == orderIds.length) &&
                (orderIds.length == ordersHash.length) &&
                (ordersHash.length == fromAddresses.length) &&
                (fromAddresses.length == tos.length) &&
                (tos.length == tokenIds.length) &&
                (tokenIds.length == amounts.length) &&
                (amounts.length == tokenURIs.length) &&
                (tokenURIs.length == names.length) &&
                (names.length == values.length),
            // (values.length == creatorAddresses.length) &&
            // (creatorAddresses.length == creatorRates.length),
            "length not same"
        );
        uint256 _total = 0;
        for (uint256 index = 0; index < values.length; index++) {
            if (types[index] == 1) {
                require(
                    values[index] == sell721List[tokenIds[index]],
                    "Incorrect price"
                );
            }
            _total += values[index];
        }
        require(_total == msg.value, "value != sum values");
        for (uint256 index = 0; index < addresses.length; index++) {
            //  require(
            //         creatorAddresses[index].length ==
            //             creatorRates[index].length,
            //         "hash is error"
            //     );
            if (types[index] == 2) {
                require(
                    keccak256(
                        abi.encodePacked(
                            bytesToHex(
                                bytes.concat(
                                    keccak256(
                                        abi.encodePacked(
                                            __salt,
                                            orderIds[index],
                                            uint2str(values[index])
                                        )
                                    )
                                )
                            )
                        )
                    ) == keccak256(abi.encodePacked(ordersHash[index])),
                    "value hash is error"
                );

                if (
                    keccak256(
                        abi.encodePacked(
                            IErc1155(addresses[index]).uri(tokenIds[index])
                        )
                    ) == keccak256("")
                ) {
                    emit Mint1155(
                        tokenIds[index],
                        amounts[index],
                        fromAddresses[index]
                    );
                    require(
                        IErc1155(addresses[index]).balanceOf(
                            fromAddresses[index],
                            tokenIds[index]
                        ) +
                            amounts[index] <=
                            supplies[index]
                    );
                    string memory s = bytesToHex(
                        bytes.concat(
                            keccak256(
                                abi.encodePacked(
                                    __salt,
                                    uint2str(tokenIds[index]),
                                    uint2str(supplies[index])
                                )
                            )
                        )
                    );
                    require(
                        keccak256(abi.encodePacked(s)) ==
                            keccak256(abi.encodePacked(suppliesHash[index])),
                        "supply value is error"
                    );
                    IErc1155(addresses[index]).mint(
                        fromAddresses[index],
                        tokenIds[index],
                        names[index],
                        tokenURIs[index],
                        amounts[index]
                    );
                } else if (
                    IErc1155(addresses[index]).balanceOf(
                        fromAddresses[index],
                        tokenIds[index]
                    ) < amounts[index]
                ) {
                    emit Mint1155(
                        tokenIds[index],
                        amounts[index] -
                            IErc1155(addresses[index]).balanceOf(
                                fromAddresses[index],
                                tokenIds[index]
                            ),
                        fromAddresses[index]
                    );
                    require(
                        IErc1155(addresses[index]).balanceOf(
                            fromAddresses[index],
                            tokenIds[index]
                        ) +
                            amounts[index] <=
                            supplies[index]
                    );
                    string memory s = bytesToHex(
                        bytes.concat(
                            keccak256(
                                abi.encodePacked(
                                    __salt,
                                    uint2str(tokenIds[index]),
                                    uint2str(supplies[index])
                                )
                            )
                        )
                    );
                    require(
                        keccak256(abi.encodePacked(s)) ==
                            keccak256(abi.encodePacked(suppliesHash[index])),
                        "supply value is error"
                    );
                    IErc1155(addresses[index]).mint(
                        fromAddresses[index],
                        tokenIds[index],
                        names[index],
                        tokenURIs[index],
                        amounts[index] -
                            IErc1155(addresses[index]).balanceOf(
                                fromAddresses[index],
                                tokenIds[index]
                            )
                    );
                }
                emit Buy(
                    addresses[index],
                    types[index],
                    tokenIds[index],
                    fromAddresses[index],
                    tos[index],
                    amounts[index],
                    values[index]
                );
                uint256 _value = values[index];
                // for (uint256 i = 0; i < creatorAddresses[i].length; i++) {
                //     address payable user = payable(creatorAddresses[index][i]);
                //     uint256 currentValue = (creatorRates[index][i] *
                //         msg.value) / 100;
                //     user.transfer(currentValue);
                //     _value = _value - currentValue;
                //     emit Creator(user, creatorRates[index][i]);
                // }
                IErc1155(addresses[index]).safeTransferFrom(
                    fromAddresses[index],
                    tos[index],
                    tokenIds[index],
                    amounts[index],
                    ""
                );
                address payable o = payable(fromAddresses[index]);
                o.transfer(_value);
            } else if (types[index] == 1) {
                // function ownerOf(uint256 tokenId) external view returns (address owner);
                address payable from = payable(
                    IErc721(addresses[index]).ownerOf(tokenIds[index])
                );
                emit Buy(
                    addresses[index],
                    types[index],
                    tokenIds[index],
                    from,
                    tos[index],
                    1,
                    values[index]
                );

                require(sell721List[tokenIds[index]] != 0, "Item not selling");
                delete sell721List[tokenIds[index]];
                IErc721(addresses[index]).safeTransferFrom(
                    IErc721(addresses[index]).ownerOf(tokenIds[index]),
                    tos[index],
                    tokenIds[index]
                );
                uint256 _value = values[index];
                // for (uint256 i = 0; i < creatorAddresses[index].length; i++) {
                //     address payable user = payable(creatorAddresses[index][i]);
                //     uint256 currentValue = (creatorRates[index][i] * msg.value) / 100;
                //     user.transfer(currentValue);
                //     _value = _value - currentValue;
                //     emit Creator(user, creatorRates[index][i]);
                // }
                from.transfer(_value);
            }
        }
    }

    function sell(
        address c,
        uint256 tokenId,
        uint256 price
    ) public {
        require(
            IErc721(c).ownerOf(tokenId) == msg.sender,
            "Permission denied:nft it's not yours"
        );
        require(price > 0, "price must more than the 0");
        sell721List[tokenId] = price;
        emit Sell721(c, tokenId, price);
    }

    function batchSell(
        address[] memory c,
        uint256[] memory tokenIds,
        uint256[] memory prices
    ) public {
        require(
            c.length == tokenIds.length && tokenIds.length == prices.length,
            "length not same"
        );
        for (uint256 index = 0; index < c.length; index++) {
            require(
                IErc721(c[index]).ownerOf(tokenIds[index]) == msg.sender,
                "Permission denied:nft it's not yours"
            );
            require(prices[index] > 0, "price must more than the 0");
            sell721List[tokenIds[index]] = prices[index];
            emit Sell721(c[index], tokenIds[index], prices[index]);
        }
    }

    function cancelSell(address c, uint256 tokenId) public {
        require(
            IErc721(c).ownerOf(tokenId) == msg.sender,
            "Permission denied:nft it's not yours"
        );
        require(sell721List[tokenId] > 0, "not selling");
        delete sell721List[tokenId];
        emit CancelSell721(c, tokenId);
    }
}

